        name: details.name,
        email: details.email,
      });
      setMode(modes[0]);

    }else{// password wrong

      if(details.password !== adminUser.password){ 
        setError("Password wrong")
      }else{
        setError("Detail wrong")
      }

      console.log("Detail do not match");
    }
  }

  const Logout = () => {//log out controll

    setUser({name:"", email:""});
    setMode("login");
    console.log("Logout");

  }

